mapper.update:

update building_info
set building_name = #{buildingName,jdbcType=VARCHAR},
  building_detail = #{buildingDetail,jdbcType=VARCHAR},
  grid_id = #{gridId,jdbcType=INTEGER},
  grid_name = #{gridName,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, building_name, building_detail, grid_id, grid_name
from building_info
where id = #{id,jdbcType=INTEGER}
